from turtle import *bgcolor("black")speed(0)penup()goto(-200, 50)pendown()def draw_circles():    for i in range(2):        for  colours in ["red", "magenta", "blue", "cyan", "green", "yellow", "white"]:            color(colours)            pensize(3)            circle(150)            forward(20)def angle_step():    left(180)    penup()    forward(170)    right(90)    forward(150)    right(180)    pendown()draw_circles()angle_step()draw_circles()angle_step()draw_circles()angle_step()draw_circles()angle_step()for a in range(10):   draw_circles()   angle_step()mainloop()